mapper.select:
        SELECT


--         级联表的字段

        FROM yaopin  a



                and a.yaopin_name like CONCAT('%',#{params.yaopinName},'%')


                and a.yaopin_types = #{params.yaopinTypes}


                  and a.yaopin_money >= #{params.yaopinMoneyStart}   


                  and a.yaopin_money <= #{params.yaopinMoneyEnd}   



        order by a.${params.orderBy} desc
